mapper.select:
SELECT COUNT(*) AS COUNT
FROM MOMENT
WHERE GROUP_ID = #{group_id}

SELECT COUNT(*) AS COUNT
FROM GR_MATCH
WHERE GROUP_ID = #{group_id}

mapper.update:
UPDATE MOMENT
SET PHASE_ID = #{phase_id}
WHERE ID = #{moment_id}

mapper.select:
SELECT COUNT(*) AS COUNT
FROM MM_SURVEY
WHERE MOMENT_ID = #{moment_id}
  AND TYPE_ID = #{type_id}
